document.addEventListener('DOMContentLoaded', function() {
    // Initialize highlight.js
    hljs.highlightAll();

    // Toggle functionality for response examples
    const toggleButtons = document.querySelectorAll('.toggle-response');

    toggleButtons.forEach(button => {
        button.addEventListener('click', function() {
            // Get the response example div that follows this button
            const responseExample = this.nextElementSibling;

            // Toggle visibility
            if (responseExample.style.display === 'block') {
                responseExample.style.display = 'none';
                this.textContent = 'View Response Example';
            } else {
                responseExample.style.display = 'block';
                this.textContent = 'Hide Response Example';
            }
        });
    });
});
